--
-- Table structure for table `book`
--

DROP TABLE IF EXISTS `book`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `book` (
  `book_id` bigint NOT NULL,
  `title` varchar(45) NOT NULL,
  `publish_date` datetime DEFAULT NULL,
  `price_standard` decimal(10,2) DEFAULT NULL,
  `price_vip` decimal(10,2) DEFAULT NULL,
  `score` decimal(3,1) DEFAULT NULL,
  `edition` varchar(45) DEFAULT NULL,
  `storage` int DEFAULT NULL,
  `class_id` int DEFAULT NULL,
  `press_id` bigint DEFAULT NULL,
  `introduction` varchar(2000) DEFAULT NULL,
  PRIMARY KEY (`book_id`),
  KEY `classification_id_idx` (`class_id`),
  KEY `press_id_idx` (`press_id`),
  CONSTRAINT `classification_id` FOREIGN KEY (`class_id`) REFERENCES `book_class` (`class_id`) ON DELETE RESTRICT ON UPDATE CASCADE,
  CONSTRAINT `press_id` FOREIGN KEY (`press_id`) REFERENCES `press` (`press_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3;
/*!40101 SET character_set_client = @saved_cs_client */;

